  const [flyer, setFlyer] = React.useState(false);
  const [flyerTwo, setFlyerTwo] = React.useState(false);
  const [flyerThree, setFlyerThree] = React.useState(false);

  //Motseki Start
  const providerOptions = {
    binancechainwallet: {
      package: true,
    },
    walletconnect: {
      package: WalletConnect, // required
      options: {
        infuraId:  process.env.INFURA_ID// required
      }
    },
  
    coinbasewallet: {
      package: CoinbaseWalletSDK, // Required
      options: {
        appName: "Coinbase", // Required
        infuraId: process.env.INFURA_ID, // Required
        chainId: 4, //4 for Rinkeby, 1 for mainnet (default)
      },
    },
  };

  const web3Modal = new Web3Modal({
    network: "rinkeby",
    theme: "light", // optional, 'dark' / 'light',
    cacheProvider: false, // optional
    providerOptions, // required
  });

  const [connectedAccount, setConnectedAccount] = useState("");

  const connectWeb3Wallet = async () => {
    try {
      const web3Provider = await web3Modal.connect();
      const library = new ethers.providers.Web3Provider(web3Provider);
      const web3Accounts = await library.listAccounts();
      setConnectedAccount(web3Accounts[0]);
    } catch (error) {
      console.log(error);
    }
  };

  const disconnectWeb3Modal = async () => {
    await web3Modal.clearCachedProvider();
    setConnectedAccount("");
  };

  // const refresh = () => window.location.reload(true)
  
  const [account, setAccount] = useState("");
  const [myBalance, setMyBalance] = useState("");
  const [mintValue, setMintValue] = useState("");

  const [stakeValue, setStakeValue] = useState("")
  const [chainError, setChainError] = useState(null);
  const toast = useToast();


  
  const [winnerName, setWinnerName] = useState('');
  const [newWinner, setNewWinner] = useState('');


  useEffect(() => {
    if (account) {
      getBalance(account);
      setChainError(null);
    }
  }, [account]);

  useEffect(() => {
    if (myBalance) {
      getBalance(myBalance);
      setChainError(null);
    }
  }, [myBalance]);
  

  const checkMetamask = async () => {
    if (isMetaMaskInstalled) {
      if (window.ethereum.chainId === "0x61") {
        const userAccount = await connect();
        console.log(userAccount);
        setAccount(userAccount[0]);
      } else {
        setChainError("Change to Binance Smart Contract Testnet");
        throw new Error("Change to Binance Smart Contract Testnet");
      }
    } else {
      throw new Error("Install metamask");
    }
  };

  const getBalance = async (myAccount) => {
    const provider = getProvider();
    const balance = await provider.getBalance(myAccount);
    setMyBalance(formatEther(balance));
    return balance;
  };

  const venusSmartContract = async () => {
    const abi = [
      // 'function getWinner() view external returns (string memory)',
      // 'function setWinner(string) returns (string)',
      'function totalSupply() external view returns (uint256)',
      'function balanceOf(address owner) external view returns (uint256 balance)'
    ];


    const signer = await getSigner();
    // Create a contract
    const venusSmartContract = new Contract(
      "0x2E7222e51c0f6e98610A1543Aa3836E092CDe62c",
      ABI,
      signer
    );
    return venusSmartContract;
  };

  const mint = async () => {
    let balance = await getBalance(account);
    balance = parseInt(balance) * 10 ** -18;

    if (balance < Number(mintValue)) {
      toast({
        position: "top-left",
        render: () => (
          <Box color="white" p={3} bg="red.500">
            Insufficient Balance!
          </Box>
        ),
      });
    }
    try {
      const smartContract = await venusSmartContract();
      const tx = await smartContract.mint({
        value: parseEther(mintValue),
        value: setStakeValue(mintValue),
      });
      const receipt = await tx.wait(1);
      if (receipt.status) {
        toast({
          position: "bottom-left",
          render: () => (
            <Box color="white" p={3} bg="green.500">
              Transaction successful
            </Box>
          ),
        });
      }
    } catch (error) {
      console.log(error);
    }
  };


  const getWinner = async () => {
    try {
      const winnerCon = await winnerContract();
      console.log(winnerCon);
      // const currentWinner = await winnerCon.getWinner();
      const currentWinner = await winnerCon.balanceOf() 
      setWinnerName(currentWinner);
      console.log(currentWinner);
    } catch (error) {
      console.log(error);
    }
  };

  const walletConnection = () => {
    try {
      checkMetamask();
    } catch (error) {
      console.log(error);
    }
  };


